import React, { useState } from 'react';

const ProjectForm = ({
  projectTitle,
  projectDescription,
  onTitleChange,
  onDescriptionChange,
  onSubmit,
}) => {
  // State for handling the project title and description
  const [localProjectTitle, setLocalProjectTitle] = useState(projectTitle || '');
  const [localDescription, setLocalDescription] = useState(projectDescription || '');

  return (
    <section className="project-form">
      <h3>Project Details</h3>
      <form onSubmit={onSubmit}>

        {/* Project Title */}
        <div style={{ width: '100%', height: '100%', position: 'relative' }}>
          <div style={{ width: 513, height: 151, left: 0, top: 0, position: 'absolute', background: '#2563EB', borderRadius: 30 }} />
          <div style={{ width: 399, left: 57, top: 40, position: 'absolute', color: 'white', fontSize: 48, fontFamily: 'Poppins', fontWeight: '500', wordWrap: 'break-word' }}>
            Add Project Title
          </div>
        </div>

        {/* Project Description */}
        <div style={{ width: '100%', height: '100%', position: 'relative', marginTop: '20px' }}>
          <div style={{ width: 1545, height: 713, left: 0, top: 0, position: 'absolute', background: '#FFFAFA', borderRadius: 34 }} />
          <div style={{ left: 57, top: 49, position: 'absolute', color: 'black', fontSize: 48, fontFamily: 'Poppins', fontWeight: '500', wordWrap: 'break-word' }}>
            Add project description
          </div>
        </div>

        {/* Other form elements */}
        <label>
          Project Title:
          <input
            type="text"
            value={localProjectTitle}
            onChange={(e) => {
              setLocalProjectTitle(e.target.value);
              onTitleChange(e);
            }}
            required
            style={{ marginTop: '20px', width: '100%', padding: '10px', fontSize: '18px', borderRadius: '5px' }}
          />
        </label>

        <label>
          Project Description:
          {/* Use the 'localDescription' state and 'setLocalDescription' function */}
          <textarea
            value={localDescription}
            onChange={(e) => {
              setLocalDescription(e.target.value);
              onDescriptionChange(e);
            }}
            required
            style={{ marginTop: '20px', width: '100%', padding: '10px', fontSize: '18px', borderRadius: '5px' }}
          />
        </label>

        <button type="submit" style={{ marginTop: '20px', padding: '15px', backgroundColor: '#2563EB', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer' }}>
          Submit Project
        </button>
      </form>
    </section>
  );
};

export default ProjectForm;
