import React, { useState } from 'react';

const ProjectForm = ({
  projectTitle,
  projectDescription,
  onTitleChange,
  onDescriptionChange,
  onSubmit,
}) => {
  // State for handling the project description
  const [description, setDescription] = useState('');

  return (
    <section className="project-form">
      
      <form onSubmit={onSubmit}>

        {/* Project Title */}
        <div style={{ paddingLeft:10,marginBottom: '0px',marginTop:'-100px',position: 'relative' }}>
          <input
            type="text"
            value={projectTitle}
            onChange={onTitleChange}
            placeholder="Add Project Title"
            required
            style={{
              width: '9%', // Adjust the width as needed
              padding: '10px',
              fontSize: '18px',
              borderRadius: '8px', // Set the border radius
             // Blue color border
              backgroundColor: '#2563EB', // Set text color
               color: 'white',  
               border: 'none', // Remove border        
              outline: 'none', // Remove outline
              }}
              
          />
                    <style>{`
            .project-form input::placeholder {
              color: white;
            }
          `}</style>

        </div>

        {/* Project Description */}
        <div style={{paddingLeft:10, marginBottom: '20px' }}>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Add project description"
            required
            style={{ width: '100%', padding: '20px', fontSize: '18px', borderRadius: '5px', border: '1px solid #FFFAFA' }}
          />
       
        </div>

        {/* Other form elements */}
         <button
          type="submit"
          style={{
            padding: '10px',
            backgroundColor: '#2563EB',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            width: '100px', // Set the width as needed
            marginLeft:10,
            marginRight: '10px', // Add margin to separate from text inputs
            fontSize: '18px', 
            font:'Poppins'
          }}
        >
          Submit
        </button>
      </form>
    </section>
  );
};

export default ProjectForm;
