import React, { useState } from 'react';

const ProjectForm = ({
  projectTitle,
  projectDescription,
  onTitleChange,
  onDescriptionChange,
  onSubmit,
}) => {
  // State for handling the project description
  const [description, setDescription] = useState('');

  return (
    <section className="project-form">
      <h3>Project Details</h3>
      <form onSubmit={onSubmit}>

        {/* Project Title */}
        <div style={{ marginBottom: '20px' }}>
          <input
            type="text"
            value={projectTitle}
            onChange={onTitleChange}
            placeholder="Add Project Title"
            required
            style={{ width: '100%', padding: '10px', fontSize: '18px', borderRadius: '5px', border: '1px solid #2563EB' }}
          />
        </div>

        {/* Project Description */}
        <div style={{ marginBottom: '20px' }}>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Add project description"
            required
            style={{ width: '100%', padding: '10px', fontSize: '18px', borderRadius: '5px', border: '1px solid #FFFAFA' }}
          />
        </div>

        {/* Other form elements */}
         <button
          type="submit"
          style={{
            padding: '10px',
            backgroundColor: '#2563EB',
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            width: '100px', // Set the width as needed
            marginRight: '10px', // Add margin to separate from text inputs
          }}
        >
          Submit
        </button>
      </form>
    </section>
  );
};

export default ProjectForm;
